mapper.select:
SELECT EXISTS(SELECT 1
              FROM chi_tiet_nhap_hang
              WHERE sys_id_chi_tiet_nhap_hang = #{sysIdChiTietNhapHang});

mapper.update:
update chi_tiet_nhap_hang
set ma_po = #{maPO}
where ma_pr = #{maPR}

update chi_tiet_nhap_hang
set sys_id_inbound = #{maInBound}
where ma_po = #{maPO}
