mapper.update:
UPDATE purchase_request
SET ngay_yeu_cau = DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%s'),
    sys_id_user  = #{nguoiYeuCau},
    trang_thai   = #{trangThai}
WHERE sys_id_pr = #{sysIdYeuCauMuaHang};

mapper.select:
SELECT EXISTS(SELECT 1
              FROM purchase_request
              WHERE sys_id_pr = #{sysIdYeuCauMuaHang});
