mapper.update:
UPDATE chi_tiet_xuat_hang
SET ma_outbound = #{maOB}
WHERE  sys_id_chi_tiet_xuat_hang = #{sysIdChiTietXuatHang}
  AND ma_outbound IS NULL

mapper.select:
SELECT EXISTS(SELECT 1
              FROM chi_tiet_xuat_hang
              WHERE sys_id_chi_tiet_xuat_hang = #{sysIdChiTietXuatHang});

SELECT so_luong
FROM chi_tiet_xuat_hang
WHERE sys_id_chi_tiet_xuat_hang = #{sysIdChiTietXuatHang}
  AND sys_id_san_pham = #{sysIdSanPham}
